#include<bits/stdc++.h>using namespace std; int main(){    int t;    cin >> t;    while(t--)    {        int a[3];        int max = 0;        int cnt = 0;         for(int i = 0; i < 3; i++)        {            cin >> a[i];            if(max < a[i])                max = a[i];        }        for(int i = 0; i < 3; i++)        {            if(a[i] == max)                cnt++;        }         for(int i = 0; i < 3; i++)        {            if(a[i] == max)            {                if(cnt == 1)                    cout << 0 << " ";                else                    cout << 1 << " ";            }            else            {                cout << max + 1 - a[i] << " ";            }        }        cout << endl;    }}