#include<bits/stdc++.h>using namespace std;int main(){    int t;    cin >> t;    while(t--)    {        int n;        cin >> n;        if(n<3)        {            vector<int> a(n);            for(int i=0;i<n;i++)            {                cin >> a[i];            }            cout << -1 << endl;        }        else        {            vector<int> a(n);            for(int i=0;i<n;i++)            {                cin >> a[i];            }            sort(a.begin(),a.end());            bool found = false;            int num =0;            for(int i=n-2;i>=1;i--)            {                if(a[i-1] == a[i] && a[i] == a[i+1])                {                    found = true;                    num = a[i];                    break;                }            }            if(found)            {                cout << num << endl;            }            else{                cout << -1 << endl;            }        }    }}