#include<bits/stdc++.h>using namespace std; int main(){    int t;    cin >> t;    for(int T=0;T<t;T++)    {        int n,can_add,des_sum;        cin >> n >> des_sum >> can_add;        vector<int> a(n);        int sum =0;        for(int i=0;i<n;i++)        {            cin >> a[i];            sum = sum + a[i];        }        if(sum <= des_sum && (des_sum-sum)%can_add==0)        {            cout << "Yes" << endl;        }        else        {            cout << "No" << endl;        }    }}