#include<bits/stdc++.h>using namespace std;Â int main(){    int t;    cin >> t;    while(t--)    {        int n;        cin >> n;        vector<int> a(n);        int max = 0;        int max_pos=0;        for(int i=0;i<n;i++)        {            cin >> a[i];        }        for(int i=0;i<n;i++)        {            max = a[i];            max_pos = i;            for(int j=i;j<n;j++)            {                if(a[j] > max)                {                    max = a[j];                    max_pos = j;                }            }            if(max > a[i])            {                reverse(a.begin()+i,a.begin()+max_pos+1);                break;            }        }        for(int i=0;i<n;i++)        {            cout << a[i] << " ";        }        cout << endl;    }}