#include<bits/stdc++.h>using namespace std; int main(){    int t;    cin >> t;    while(t--)    {    long long n,q;    cin >> n >> q;    vector<long long> a(n);    vector<long long> b(n);    vector<long long> c(n);    for(int i=0;i<n;i++)    {        cin >> a[i];    }    for(int i=0;i<n;i++)    {        cin >> b[i];    }    for(int i=0;i<n;i++)    {        c[i] = max(a[i],b[i]);    }    for(int i=n-2;i>=0;i--)    {        c[i] = max(c[i+1],c[i]);    }    vector<long long> fin(n+1);    for(int i=0;i<n;i++)    {        fin[i+1] = fin[i] + c[i];    }    while(q--)    {        int l,r;        cin >> l >> r;        l--;        r--;        long long sum = fin[r+1] -fin[l];        cout << sum << " ";    }    cout << endl;    }}