#include<bits/stdc++.h>using namespace std; int main(){    int n;    cin >> n;    vector<int> a(n);    int even=0 , odd=0;    for(int i=0;i<n;i++)    {        cin >> a[i];        if(a[i]%2==0)        {            even++;        }        else        {            odd++;        }    }    int ans=0;    for(int i=0;i<n;i++)    {        if(even > odd)        {            if(a[i]%2==1)            {                ans = i;            }        }        else        {            if(a[i]%2==0)            {                ans =i;            }        }    }    cout << ans+1 << endl;}