#include <iostream>using namespace std; int main() {    int n, m;    cin >> n >> m;     for (int i = 1; i <= n; i++) {        if (i % 2 == 1) {            // Odd row: full of '#'            for (int j = 0; j < m; j++) cout << '#';        } else {            // Even row            if ((i / 2) % 2 == 1) {                // '#' at the end                for (int j = 0; j < m - 1; j++) cout << '.';                cout << '#';            } else {                // '#' at the beginning                cout << '#';                for (int j = 0; j < m - 1; j++) cout << '.';            }        }        cout << '\n';    }     return 0;}